<?php/* Add this on all pages on top. */
set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';
require_once 'config/smarty.php';/** * Check for login */require_once 'includes/auth.php';require_once 'class/account.php';$accountObject = new class_account();  if(isset($_GET['delete_code'])) {		$errorArray				= array();	$errorArray['error']	= '';	$errorArray['result']	= 0;		$formValid				= true;	$success					= NULL;	$code						= trim($_GET['delete_code']);			if($errorArray['error']  == '' && $errorArray['result']  == 0 ) {		$data	= array();		$data['account_deleted'] = 1;		$data['account_code'] = $code;				$where = $accountObject->getAdapter()->quoteInto('account_code = ?', $code);		$success	= $accountObject->update($data, $where);					if(is_numeric($success) && $success > 0) {			$errorArray['error']	= '';			$errorArray['result']	= 1;					} else {			$errorArray['error']	= 'Could not delete, please try again.';			$errorArray['result']	= 0;						}	}		echo json_encode($errorArray);	exit;}/* Setup Pagination. */if(isset($_GET['action']) && trim($_GET['action']) == 'accountsearch') {	$search = isset($_REQUEST['search']) && trim($_REQUEST['search']) != '' ? trim($_REQUEST['search']) : null;	$start 	= isset($_REQUEST['iDisplayStart']) ? $_REQUEST['iDisplayStart'] : 0;	$length = isset($_REQUEST['iDisplayLength']) ? $_REQUEST['iDisplayLength'] : 20;		$jobData = $accountObject->getSearch($search, $start, $length);	$alljobs = array();	if($jobData) {		for($i = 0; $i < count($jobData['records']); $i++) {			$item = $jobData['records'][$i];			$alljobs[$i] = array(						($item['account_image_path'] == '' ? '<img src="/images/avatar.jpg" width="60" />' : '<img src="http://www.imibutho.co.za'.$item['account_image_path'].'tny_'.$item['account_image_name'].$item['account_image_ext'].'" width="60" />'),							$item['account_added'],												$item['areapost_name'], 						'<a class="'.($item['account_active'] == 0 ? 'error' : 'success').'" href="/account/view/details.php?code='.$item['account_code'].'">'.$item['account_name'].'</a>', 						$item['account_email'],						'<button onclick="javascript:deleteitem(\''.$item['account_code'].'\');">Delete</button>');		}	}					if($jobData) {		$response['sEcho'] = $_REQUEST['sEcho'];		$response['iTotalRecords'] = $jobData['displayrecords'];				$response['iTotalDisplayRecords'] = $jobData['count'];		$response['aaData']	= $alljobs;	} else {		$response['result'] 	= false;		$response['message']	= 'There are no items to show.';				}	    echo json_encode($response);    die();	}/* End Pagination Setup. */$smarty->display('account/view/default.tpl');?>