<?php/* Add this on all pages on top. */
set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';
require_once 'config/smarty.php';/** * Check for login */require_once 'includes/auth.php';require_once 'class/video.php';$videoObject = new class_video();  if(isset($_GET['delete_code'])) {		$errorArray				= array();	$errorArray['error']	= '';	$errorArray['result']	= 0;		$formValid				= true;	$success					= NULL;	$code						= trim($_GET['delete_code']);			if($errorArray['error']  == '' && $errorArray['result']  == 0 ) {			$data	= array();		$data['video_deleted'] = 1;				$where = $videoObject->getAdapter()->quoteInto('video_code = ?', $code);		$success	= $videoObject->update($data, $where);					if(is_numeric($success) && $success > 0) {			$errorArray['error']	= '';			$errorArray['result']	= 1;					} else {			$errorArray['error']	= 'Could not delete, please try again.';			$errorArray['result']	= 0;						}	}		echo json_encode($errorArray);	exit;}/* Setup Pagination. */if(isset($_GET['action']) && trim($_GET['action']) == 'videosearch') {	$search = isset($_REQUEST['search']) && trim($_REQUEST['search']) != '' ? trim($_REQUEST['search']) : null;	$start 	= isset($_REQUEST['iDisplayStart']) ? $_REQUEST['iDisplayStart'] : 0;	$length = isset($_REQUEST['iDisplayLength']) ? $_REQUEST['iDisplayLength'] : 20;		$jobData = $videoObject->getSearch($search, $start, $length);	$alljobs = array();	if($jobData) {		for($i = 0; $i < count($jobData['records']); $i++) {			$item = $jobData['records'][$i];			$alljobs[$i] = array(						$item['video_added'],						'<iframe title="'.$item['video_name'].'" class="youtube-player" type="text/html" width="225" height="149" src="'.$item['video_url'].'" frameborder="0" allowFullScreen></iframe>',						'<a href="/video/details.php?code='.$item['video_code'].'">'.$item['video_name'].'</a>',						'<button onclick="javascript:deleteitem(\''.$item['video_code'].'\');">Delete</button>');		}	}					if($jobData) {		$response['sEcho'] = $_REQUEST['sEcho'];		$response['iTotalRecords'] = $jobData['displayrecords'];				$response['iTotalDisplayRecords'] = $jobData['count'];		$response['aaData']	= $alljobs;	} else {		$response['result'] 	= false;		$response['message']	= 'There are no items to show.';				}	    echo json_encode($response);    die();	}/* End Pagination Setup. */$smarty->display('video/default.tpl');?>