<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/* Get parameter. */$code = isset($_REQUEST['code']) && trim($_REQUEST['code']) != '' ? trim($_REQUEST['code']) : '';if($code == '') {		/* Get the hell out of here. */		header('Location: /');		exit;	} else {		require_once 'class/participant.php';		/* Object. */	$participantObject 			= new class_participant();		/* Fetch the use. */	$participantloginData = $participantObject->_participantlogin->getByHash($code, 0);	if($participantloginData) {		/* Activate account and stay on this page. */		$data = array('participant_active' => 1, 'participant_code' => $participantloginData['participant_code']);		/* Update details. */		$success = $participantObject->updateParticipant($data, 'EMAIL');				if($success) {			$smarty->assign('participantloginData', $participantloginData);		} else {			echo 'Could not activate you, please email us on info@imibutho.co.za';			exit;		}	} else {		/* Get the hell out of here. */		header('Location: /');		exit;	}}//display template$smarty->display('registration/activate/default.tpl');?>