<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/* Get parameter. */$code = isset($_REQUEST['code']) && trim($_REQUEST['code']) != '' ? trim($_REQUEST['code']) : '';if($code == '') {		/* Get the hell out of here. */		header('Location: /');		exit;	} else {		require_once 'class/account.php';		/* Object. */	$accountObject 			= new class_account();		/* Fetch the use. */	$accountData = $accountObject->getByHash($code, 0);	if($accountData) {		/* Activate account and stay on this page. */		$data = array('account_active' => 1);		/* Update details. */		$where		= $accountObject->getAdapter()->quoteInto('account_code = ?', $accountData['account_code']);		$accountObject->update($data, $where);						$smarty->assign('accountData', $accountData);	} else {		/* Get the hell out of here. */		header('Location: /');		exit;	}}//display template$smarty->display('registration/activate/default.tpl');?>